import { useState, useEffect, useRef } from "react";
import axios from "axios";

import SteelSeriesImgSrc from "../../assets/images/steelserieslogo.png";
import "./styles//title.scss"
import WaveSeparator from "./WaveSeparator";

const Title = () => {

  const videoFrame = useRef<HTMLVideoElement>(null)
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const [index, setIndex] = useState(1);
  const toRotate = ["Software Engineer", "Full Stack Developper", "Video Game Developper"];
  const period = 2000;
  const [videoUrl, setVideoUrl] = useState('');


  useEffect(() => {
    axios.get('/api/videos/1/')
      .then(response => {
        setVideoUrl(response.data.file);
      })
      .catch(error => {
        console.error('Error fetching video:', error);
      });
  }, []);

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setIndex((prevIndex) => prevIndex - 1);
      setDelta(period);
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setIndex(1);
      setDelta(500);
    } else {
      setIndex((prevIndex) => prevIndex + 1);
    }
  };

  return (
    <div>
      <div className="title-wrapper">
        <div className="video-container ">
          <video ref={videoFrame} className="video" src={videoUrl}/>
        </div>
        <div style={{height : videoFrame.current?.offsetHeight}} className="title">
            <div className="name">Pierre-RaphaÃ«l Wozny</div>
            <div className="job">{text}</div>
        </div>
      </div>
      <WaveSeparator videoFrameHeight={videoFrame.current?.offsetHeight}/>
    </div>
  );  
};

export default Title;
